T = T * 10

m = 30
n = 9999
f = 11

for i in range(n):

    newPage(210, 297)
    w = width() - (3 * m)
    h = height() - (3 * m)

    # right page
    if i % 2:
        fill(1, 0, 0)
        a = 'left'
        px = width() - m
        x = 2 * m
        f -= 1

    # left page
    else:
        fill(0, 0, 1)
        a = 'right'
        px = m
        x = m

    fontSize(f)
    T = textBox(T, (x, m * 2, w, h), align=a)
    text(str(i + 1), (px, m))

    if len(T) == 0:
        break

